import { ProjectForm, CalculationResult } from './types';

// Базовые ставки для разных типов работ (в долларах)
const BASE_RATES = {
  presentation: 70,
  website_design: 200,
  landing_page: 160,
  logo: 110,
  branding: 350,
  social_media: 40,
  print: 55,
  illustration: 80,
  ui_ux: 280,
  delegated_support: 25,
  web_development: 400
};

// Коэффициенты для конкретных дизайнеров
const DESIGNER_MULTIPLIERS = {
  alex: 1.4,      // Alex - опытный дизайнер
  maria: 1.2,     // Maria - средний уровень
  dmitry: 1.6,    // Dmitry - высокий уровень
  anna: 1.1,      // Anna - начинающий
  sergey: 1.3,    // Sergey - опытный
  elena: 1.0,     // Elena - базовый уровень
  andrey: 1.5,    // Andrey - высокий уровень
  natalia: 1.2    // Natalia - средний уровень
};

// Коэффициенты для регионов мира
const REGION_MULTIPLIERS = {
  north_america: 1.8,    // Северная Америка - высокие ставки
  europe: 1.5,           // Европа - высокие ставки
  asia: 1.2,             // Азия - средние ставки
  middle_east: 1.4,      // Ближний Восток - выше среднего
  africa: 0.8,           // Африка - ниже среднего
  south_america: 1.0,    // Южная Америка - средние ставки
  australia_oceania: 1.6, // Австралия и Океания - высокие ставки
  cis: 1.1               // СНГ - чуть выше среднего
};

// Коэффициенты для источников
const SOURCE_MULTIPLIERS = {
  fiverr: 1.3,
  upwork: 1.2,
  freelancer: 1.0,
  telegram: 0.9,
  internal: 1.0,
  other: 1.0
};

// Коэффициент срочности
const URGENCY_MULTIPLIER = 1.5;

// Коэффициент количества элементов
const ELEMENTS_MULTIPLIER = 0.1; // +10% за каждый элемент

// Маржа ВиТрио (коэффициент)
const COMPANY_MARGIN = 1.3;

export function calculateProjectCost(form: ProjectForm): CalculationResult {
  // Базовая стоимость за тип работы
  const baseRate = BASE_RATES[form.workType as keyof typeof BASE_RATES] || 70;

  // Применяем коэффициенты
  const designerMultiplier = DESIGNER_MULTIPLIERS[form.designer as keyof typeof DESIGNER_MULTIPLIERS] || 1.0;
  const regionMultiplier = REGION_MULTIPLIERS[form.region as keyof typeof REGION_MULTIPLIERS] || 1.0;
  const sourceMultiplier = SOURCE_MULTIPLIERS[form.source as keyof typeof SOURCE_MULTIPLIERS] || 1.0;

  // Коэффициент срочности
  const urgencyMultiplier = form.isUrgent ? URGENCY_MULTIPLIER : 1.0;

  // Коэффициент количества элементов
  const elementsMultiplier = 1 + (form.elementsCount * ELEMENTS_MULTIPLIER);

  // Рассчитываем стоимость работы дизайнера
  const designerPrice = Math.round(
    baseRate * 
    designerMultiplier * 
    regionMultiplier * 
    sourceMultiplier * 
    urgencyMultiplier * 
    elementsMultiplier
  );

  // Рассчитываем стоимость для клиента (с маржей компании)
  const clientPrice = Math.round(designerPrice * COMPANY_MARGIN);

  return {
    clientPrice,
    designerPrice
  };
}

// Функция для форматирования цены в долларах
export function formatPrice(price: number): string {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 0,
    maximumFractionDigits: 0
  }).format(price);
}
