import React, { useState, useEffect } from 'react';
import { ProjectForm, CalculationResult } from './types';
import { SOURCE_OPTIONS, WORK_TYPE_OPTIONS, REGION_OPTIONS, DESIGNER_OPTIONS } from './types';
import { calculateProjectCost, formatPrice } from './calculator';

const App: React.FC = () => {
  const [form, setForm] = useState<ProjectForm>({
    source: '',
    workType: '',
    elementsCount: 1,
    isUrgent: false,
    region: '',
    designer: ''
  });

  const [results, setResults] = useState<CalculationResult | null>(null);

  // Автоматический расчет при изменении формы
  useEffect(() => {
    if (form.source && form.workType && form.region && form.designer) {
      const calculatedResults = calculateProjectCost(form);
      setResults(calculatedResults);
    } else {
      setResults(null);
    }
  }, [form]);

  const handleInputChange = (field: keyof ProjectForm, value: string | number | boolean) => {
    setForm(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleReset = () => {
    setForm({
      source: '',
      workType: '',
      elementsCount: 1,
      isUrgent: false,
      region: '',
      designer: ''
    });
    setResults(null);
  };

  return (
    <div className="container">
      <div className="header">
        <h1>Design Calculator</h1>
        <p>Калькулятор стоимости дизайн-проектов для координаторов ВиТрио</p>
      </div>

      <div className="main-content">
        <div className="form-section">
          <div className="form-group">
            <label className="form-label">Источник работы</label>
            <select
              className="form-select"
              value={form.source}
              onChange={(e) => handleInputChange('source', e.target.value)}
            >
              <option value="">Выберите источник</option>
              {SOURCE_OPTIONS.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label className="form-label">Тип работы</label>
            <select
              className="form-select"
              value={form.workType}
              onChange={(e) => handleInputChange('workType', e.target.value)}
            >
              <option value="">Выберите тип работы</option>
              {WORK_TYPE_OPTIONS.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label className="form-label">Количество элементов дизайна</label>
            <input
              type="number"
              className="form-input"
              min="1"
              value={form.elementsCount}
              onChange={(e) => handleInputChange('elementsCount', parseInt(e.target.value) || 1)}
            />
          </div>

          <div className="form-group">
            <label className="form-label">Срочность проекта</label>
            <div className="radio-group">
              <label className="radio-item">
                <input
                  type="radio"
                  name="urgency"
                  checked={form.isUrgent === false}
                  onChange={() => handleInputChange('isUrgent', false)}
                />
                <span>Нет</span>
              </label>
              <label className="radio-item">
                <input
                  type="radio"
                  name="urgency"
                  checked={form.isUrgent === true}
                  onChange={() => handleInputChange('isUrgent', true)}
                />
                <span>Да</span>
              </label>
            </div>
          </div>

          <div className="form-group">
            <label className="form-label">Регион задачи</label>
            <select
              className="form-select"
              value={form.region}
              onChange={(e) => handleInputChange('region', e.target.value)}
            >
              <option value="">Выберите регион</option>
              {REGION_OPTIONS.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label className="form-label">Предполагаемый дизайнер</label>
            <select
              className="form-select"
              value={form.designer}
              onChange={(e) => handleInputChange('designer', e.target.value)}
            >
              <option value="">Выберите дизайнера</option>
              {DESIGNER_OPTIONS.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>

          <div className="button-group">
            <button 
              className="btn btn-secondary" 
              onClick={handleReset}
              title="Сбросить все поля"
            >
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" style={{ marginRight: '8px' }}>
                <path d="M3 12a9 9 0 0 1 9-9 9.75 9.75 0 0 1 6.74 2.74L21 8"/>
                <path d="M21 3v5h-5"/>
                <path d="M21 12a9 9 0 0 1-9 9 9.75 9.75 0 0 1-6.74-2.74L3 16"/>
                <path d="M3 21v-5h5"/>
              </svg>
              Сбросить форму
            </button>
          </div>
        </div>

        <div className="results-section">
          <div className="results">
            <div className="result-item">
              <span className="result-label">Стоимость проекта для ВиТрио</span>
              <span className="result-value">
                {results ? formatPrice(results.clientPrice) : '$0'}
              </span>
            </div>
            <div className="result-item">
              <span className="result-label">Стоимость работы дизайнера</span>
              <span className="result-value">
                {results ? formatPrice(results.designerPrice) : '$0'}
              </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
